css:
.product__payment>a{
  display: none !important;
}
@media (max-width: 450px){
.header__logo {
    max-width: 180px;
    max-height: 60px;
  }}

.header__search-btn:after {
    font-size: 20px;
    content: "OK";
    font-weight: 500;
    color: #454a47;
    text-transform: lowercase;
    text-decoration: underline;
}
.header__nav a {
    font-weight: 700;
    color: #9e0017;
    padding: 7px;
    border-radius: 0px !important;
}
.header__nav a:hover {
    background: var(--primary-lightest);
    text-decoration: none;
    background: #9e0017;
    color: white;
    font-weight: 600;
}
.menu__item:hover {
    text-decoration: none;
    background: var(--primary-lightest);
    background: #9e0017;
    color: white;
    font-weight: 700;
}
.search__input {
    padding-right: var(--spacer-5);
    border-radius: 0px;
}
.search-engine__info {
    padding-bottom: var(--spacer-3);
    text-transform: uppercase;
    color: #9e0017 !important;
    text-decoration: underline;
}
.btn-primary {
    color: #fff;
    background-color: #3c9430;
    border-color: #333;
    border-radius: 0px !important;
    border: none;
}
.product__discount {
  display: none !important;
}
.variations__grid {
    margin-bottom: var(--spacer-3);
    text-transform: uppercase;
    margin-top: 30px;
}
.product-card__offer-stamp{
  display:none !important;
}
.product__name{
  font-size: 23px !important;
}
.prices--big .prices__value{
  font-size: 30px !important;
}
