css:
.product__payment>a{
  display: none !important;
}

.prices__compare{
  font-weight: 400;
  color: #f37c7c;
  font-size: 14px;
}
.login__oauth{
  display: none;
}
.share {
    /*display: none !important;*/
}


.app__contact { 
  display: none;
}


.minicart__subtotal { 
  color : #333 !important;
}


.product-card__name {
  margin-bottom: 0px !important;
}

.product__prices .prices__value:before {
    content: "" !important;
}
.prices__installments small {
  text-transform: none;
}

.product-card__prices .prices__installments span:before{
  font-size: 15px;
  color: black;
  font-weight: 300 !important;
}

.product-card__prices .prices__installments{
margin-bottom: 15px !important;
    /* font-size: 16px !important; */
    border-bottom: 1px solid #f1f1f1;
    padding-bottom: 5px;
    border-left: 1px solid #f1f1f1;
    border-right: 1px solid #f1f1f1;
}
.product-card__prices .prices__installments:before{
  content: "" !important;
}


.product-card__prices .prices__value {
    color: #333;
    font-size: 12px !important;
    padding-top: 7px !important;
}

.product-card__prices .prices__value:first-child {
    /* margin-top: calc(var(--line-height)*var(--font-size)); */
    margin-top: -8px !important;
}
@media (max-width: 450px){
  .top-bar__countdown{
    font-weight:800 !important;
  }
.header__logo {
    max-width: 145px;
    max-height: 60px;
  }}

.header__search-btn:after {
    font-size: 20px;
    content: "OK";
    font-weight: 500;
    color: #454a47;
    text-transform: lowercase;
    text-decoration: underline;
}
.header__nav a {
    font-weight: 700;
    color: #9e0017;
    padding: 7px;
    border-radius: 0px !important;
}
.header__nav a:hover {
    background: var(--primary-lightest);
    text-decoration: none;
    background: #9e0017;
    color: white;
    font-weight: 600;
}
.menu__item:hover {
    text-decoration: none;
    background: var(--primary-lightest);
    background: #9e0017;
    color: white;
    font-weight: 700;
}
.search__input {
    padding-right: var(--spacer-5);
    border-radius: 0px;
}
.search-engine__info {
    padding-bottom: var(--spacer-3);
    text-transform: uppercase;
    color: #9e0017 !important;
    text-decoration: underline;
}
.btn-primary {
    color: #fff;
    background-color: #4ba02d;
    border-color: #4ba02d;
    border-radius: 0px !important;
    border: none;
}
.product__discount {
  display: none !important;
}
.variations__grid {
    margin-bottom: var(--spacer-3);
    text-transform: uppercase;
    margin-top: 30px;
}
.product-card__offer-stamp{
  display:none !important;
}
.product__name{
  font-size: 21px !important;
  font-weight: 300 !important;
  margin-bottom: 9px !important;
}



#product-description > div > p{
  /*display:none !important;*/
}

.h5, h5{
  font-size: 16px;
}

.product__buy .btn-primary{
width: 100% !important;
    text-transform: initial;
    height: 49px !important;
    font-size: 22px;
    font-weight: 600;
    padding-bottom: 9px;
    background-color: #009277;
    border-color: #009277;
}
.product__buy .btn-primary:hover{
    background-color: #043129;
    border-color: #043129;
}

#product-actions > div > div.product__buy > span > button > span > i{
  display: none !important;
}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover {
    color: #fff;
    background-color: #29e466;
    /* border-color: #1a1a1a; */
}
.prices--big .prices__value {
  font-size: 21px !important;
  color: #131513;
  font-weight: 400 !important;
}
.product-card__name {
    font-size: 12px!important;
    padding-bottom: 0px !important;
}
.banners-grid{
  text-align : center !important;
}
.product-card__buy .btn {
  display: none !important;
}

.product__short-stock{
  display:none !important;
}

footer .header__logo {
    margin-bottom: .5rem;
    width: auto;
    height: 27px;
}
.order-lg-first p {
  font-size: 12px !important;
}

@media (min-width: 992px){
.top-bar__countdown {
    font-size: var(---font-size-lg);
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
    font-weight: 800;

  } 
 }
.product__sku{
  display: none !important;
}
.products-carousel__title {
    color: #727272;
    text-transform: uppercase;
    color: #971819;
    font-weight: 500;
}
.glide__bullet{
  display: none !important;
}

.minicart__btn-checkout, .cart__btn-checkout {
    text-transform: uppercase;
    font-weight: 700;
    background-color: #009277;
    height: 60px;
    padding-top: 19px;
    font-size: 18px;
}

.product-card__prices .prices__installments:before {
    content: "em";
}

#pppContinue, #checkout > div > span > div > div > div.col-lg-8 > div > div:nth-child(2) > div > div.payment > span > div > div > div > button{
    text-transform: uppercase;
    font-weight: 700;
    background-color: #009277;
    height: 60px;
    /* padding-top: 16px; */
    font-size: 18px;
}
.product-card__prices .prices__value:before{
  display: none !important;
}
#_trustvox_widget{
  display: none !important;
}
#trustvox-rating{
  display: none !important;
}

#ts-shelf-container{
  display: none !important;
}



.shipping-calculator__free-from-value .progress-bar {
    background-color: #1d934c !important;
    text-transform: uppercase !important;
}
html body .ts-shelf-container{
  display: none !important;
}
.prices.product-card__prices, .product-card__name
  padding-bottom: 15px;
}
.product__out-of-stock, .product__unavailable {
    text-align: center;
    font-weight: var(--font-bold);
    font-size: var(--font-size-lg);
    color: var(--warning);
    background: red;
    color: white;
    text-transform: uppercase;
}
#cart > div > span > div > div.col-md-5.col-lg-4.mt-3.mt-md-0 > div.cart__info > div.shipping-calculator.cart__shipping > div > div > span{
  text-transform: uppercase;
  font-size: 12px;
}

.product__unavailable {
  font-size: 0px
}
.product__unavailable::after {
  content: "Vendemos todo o estoque. Veja outros modelos em nossa loja.";
  font-size: 1rem
}
.checkout__btn-proceed{
  text-transform: uppercase;
}
.shipping-calculator__services .active {
    cursor: auto;
    text-transform: uppercase;
    background: #f1f1f1;
    color: #333;
}
.prices__discount{
  display: none !important;
}
#product-actions > div > p > div > span > div.prices__installments > small:nth-child(1), #cart > div > span > div > div.col-md-5.col-lg-4.mt-3.mt-md-0 > div.cart__info > div.cart__summary-row.cart__total > div > span > div > small:nth-child(1){
  display: none !important;
}
.products-carousel__title a, .products-carousel__title span{
  font-size: 16px !important;
}
#product-actions > div > div.shipping-calculator{
    padding: 10px;
    background: #f1f1f1;
}
#cart-quickview > aside > footer > div > strong > div > strong, #cart > div > span > div > div.col-md-5.col-lg-4.mt-3.mt-md-0 > div.cart__info > div.cart__summary-row.cart__total > div > strong, #checkout > div > span > div > div > div.col-lg-4.checkout__info > div > article > div.summary__amount > div.summary__amount__row.summary__amount__row--total > div > strong{
  font-weight: 300 !important;
  color: black !important;
}
.variations__option{
  background: white;
    border-radius: 50px;
}
#confirmation > div > span > div > div > div.col-md-7.col-lg-9 > div > div:nth-child(1) > article > div > div.order-info__payment.card > div.card-body > p.order-info__payment-notes.alert.alert-warning{
color: #ffffff;
    background-color: #ff0101;
    border-color: #ffeeba;
    font-weight: 700;
    text-transform: uppercase;
    padding: 7px;
    font-size: 17px;
}
#cart-quickview > aside > footer > div > strong > div > span.prices__compare > small:nth-child(3), #cart-quickview > aside > footer > div > strong > div > span.prices__compare > small:nth-child(1){
  display: none !important;
}
#product-actions > div > div.shipping-calculator > div > span > div > div > span > div > span{
text-transform: uppercase;
    text-decoration: underline;
    /*background: green;
    color: white;*/
    font-weight: 600;
    padding: 5px;
}

#product > div > div.col > p.product__info.lead {
    display: none !important;
    margin-top: 26px;
    padding: 10px;
    background: #ffe6e6 !important;
    font-weight: 500;
    text-align: center;
    font-size: 13px !important;
    text-transform: none !important;
}
#main > div.ecom-credits > a:nth-child(2){
  display:none !important;
}
