css:
.product__payment>a{
  display: none !important;
}
@media (max-width: 450px){
.header__logo {
    max-width: 180px;
    max-height: 60px;
  }}

.header__search-btn:after {
    font-size: 20px;
    content: "OK";
    font-weight: 500;
    color: #454a47;
    text-transform: lowercase;
    text-decoration: underline;
}
.header__nav a {
    font-weight: 700;
    color: #9e0017;
    padding: 7px;
    border-radius: 0px !important;
}
.header__nav a:hover {
    background: var(--primary-lightest);
    text-decoration: none;
    background: #9e0017;
    color: white;
    font-weight: 600;
}
.menu__item:hover {
    text-decoration: none;
    background: var(--primary-lightest);
    background: #9e0017;
    color: white;
    font-weight: 700;
}
.search__input {
    padding-right: var(--spacer-5);
    border-radius: 0px;
}
.search-engine__info {
    padding-bottom: var(--spacer-3);
    text-transform: uppercase;
    color: #9e0017 !important;
    text-decoration: underline;
}
.btn-primary {
    color: #fff;
    background-color: #3c9430;
    border-color: #3c9430;
    border-radius: 0px !important;
    border: none;
}
.product__discount {
  display: none !important;
}
.variations__grid {
    margin-bottom: var(--spacer-3);
    text-transform: uppercase;
    margin-top: 30px;
}
.product-card__offer-stamp{
  display:none !important;
}
.product__name{
  font-size: 1.2em !important;
}
.prices--big .prices__value{
  font-size: 30px !important;
}
.h5, h5{
  font-size: 16px;
}
.product__buy .btn-primary{
  width: 100% !important;
}
.prices__value{
  font-size: 16px !important;
  font-weight: 600 !important;
}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover {
    color: #fff;
    background-color: #29e466;
    /* border-color: #1a1a1a; */
}
.prices--big .prices__value {
    font-size: 27px !important;
    color: #29e466;
}
.product-card__name {
  font-size: 12px !important;
}
.banners-grid{
  text-align : center !important;
}
.product-card__buy .btn {
  display: none !important;
}
@media screen and (max-width: 1367px) and (min-width: 998px)
.product-card--small .product-card__picture img, .product-card--small .product-card__pictures {
    max-height: 500px!important;
    height: 362px!important;
    width: 112%;
    text-align: center;
}
